using Microsoft.AspNetCore.Mvc;
using xbytechat.api.Helpers;
using Serilog;
using System.Security.Claims;
using xbytechat.api.Features.BusinessModule.DTOs;
using xbytechat.api.Features.BusinessModule.Models;
using xbytechat.api.Features.BusinessModule.Services;
using Microsoft.AspNetCore.Authorization;

namespace xbytechat.api.Features.BusinessModule.Controllers
{
    [ApiController]
    [Route("api/[controller]")]
    public class BusinessesController : ControllerBase
    {
        private readonly IBusinessService _businessService;

        public BusinessesController(IBusinessService businessService)
        {
            _businessService = businessService;
        }



        [HttpGet("pending")]
        public async Task<IActionResult> GetPendingBusinesses()
        {
            try
            {
                var role = HttpContext.User.Claims
                    .FirstOrDefault(c => c.Type == ClaimTypes.Role)?.Value;
                var userId = HttpContext.User.Claims.FirstOrDefault(c => c.Type == "id")?.Value ?? "";

                if (!new[] { "admin", "superadmin", "partner" }.Contains(role))
                {
                    return StatusCode(403, ResponseResult.ErrorInfo("‚õî Access denied: You are not authorized to view pending businesses."));
                }

                var result = await _businessService.GetPendingBusinessesAsync(role, userId);

                return Ok(ResponseResult.SuccessInfo("‚úÖ Pending businesses fetched successfully.", result));
            }
            catch (Exception ex)
            {
                return StatusCode(500, ResponseResult.ErrorInfo("‚ùå Failed to fetch pending businesses. Please try again later."));
            }
        }


        // ‚úÖ Get business by ID (used for profile completion)

        [HttpGet("{id}")]
        public async Task<IActionResult> GetBusinessById(Guid id)
        {
            try
            {
                var business = await _businessService.GetByIdAsync(id);
                if (business == null)
                    return NotFound(ResponseResult.ErrorInfo("‚ùå Business not found."));

                return Ok(business);
            }
            catch (Exception ex)
            {
                return StatusCode(500, ResponseResult.ErrorInfo("üö® Failed to fetch business. " + ex.Message));
            }
        }

        [HttpPut("assigned-to/{id}")]
        public async Task<IActionResult> UpdateBusiness(Guid id, [FromBody] Business business)
        {
            if (id != business.Id)
            {
                return BadRequest(new { message = "‚ùå ID mismatch." });
            }

            var result = await _businessService.UpdateBusinessAsync(business);
            if (!result.Success)
            {
                return BadRequest(result);
            }

            return Ok(result);
        }


        // üü¢ Approve a business
        [HttpPost("approve/{id}")]
        public async Task<IActionResult> Approve(Guid id)
        {
            try
            {
                var result = await _businessService.ApproveBusinessAsync(id);

                if (result.Success)
                {
                    // ‚úÖ Optional Success Logging
                    Log.Information("‚úÖ Business approved successfully. BusinessId: {BusinessId}", id);
                    return Ok(result);
                }
                else
                {
                    // ‚úÖ Optional Warning Logging
                    Log.Warning("‚ö†Ô∏è Business approval failed. BusinessId: {BusinessId} - Message: {Message}", id, result.Message);
                    return BadRequest(result);
                }
            }
            catch (Exception ex)
            {
                // ‚úÖ Proper Error Logging
                Log.Error(ex, "‚ùå Exception occurred while approving business. BusinessId: {BusinessId}", id);

                return StatusCode(500, ResponseResult.ErrorInfo(
                    "‚ùå Something went wrong while approving business. Please try again later."
                ));
            }
        }


        // üî¥ Reject a business
        [HttpPost("reject/{id}")]
        public async Task<IActionResult> Reject(Guid id)
        {
            try
            {
                var result = await _businessService.RejectBusinessAsync(id);
                return result.Success ? Ok(result) : NotFound(result);
            }
            catch (Exception ex)
            {
                return StatusCode(500, ResponseResult.ErrorInfo("‚ùå Failed to reject business. " + ex.Message));
            }
        }

        // üü° Put a business on hold
        [HttpPost("hold/{id}")]
        public async Task<IActionResult> Hold(Guid id)
        {
            try
            {
                var result = await _businessService.HoldBusinessAsync(id);
                return result.Success ? Ok(result) : NotFound(result);
            }
            catch (Exception ex)
            {
                return StatusCode(500, ResponseResult.ErrorInfo("‚ùå Failed to hold business. " + ex.Message));
            }
        }

        // üõ† Complete profile after signup
        [HttpPost("profile-completion/{businessId}")]
        public async Task<IActionResult> CompleteProfile(Guid businessId, [FromBody] ProfileCompletionDto dto)
        {
            try
            {
                var result = await _businessService.CompleteProfileAsync(businessId, dto);
                return result.Success ? Ok(result) : BadRequest(result);
            }
            catch (Exception ex)
            {
                return StatusCode(500, ResponseResult.ErrorInfo("‚ùå Failed to update profile. " + ex.Message));
            }
        }

        [HttpGet("approved")]
        [Authorize(Roles = "SuperAdmin")]
        public async Task<IActionResult> GetApprovedBusinesses()
        {
            var result = await _businessService.GetApprovedBusinessesAsync();
            return Ok(result);
        }

    }
}

