namespace xbytechat.api.Features.BusinessModule.Controllers
{
    [ApiController]
    [Route("api/[controller]")]
    public class BusinessesController : ControllerBase
    {
        private readonly IBusinessService _businessService;

        public BusinessesController(IBusinessService businessService)
        {
            _businessService = businessService;
        }



        [HttpGet("pending")]
        public async Task<IActionResult> GetPendingBusinesses()
        {
            try
            {
                var role = HttpContext.User.Claims
                    .FirstOrDefault(c => c.Type == ClaimTypes.Role)?.Value;
                var userId = HttpContext.User.Claims.FirstOrDefault(c => c.Type == "id")?.Value ?? "";

                if (!new[] { "admin", "superadmin", "partner" }.Contains(role))
                {
                    return StatusCode(403, ResponseResult.ErrorInfo("â›” Access denied: You are not authorized to view pending businesses."));
                }

                var result = await _businessService.GetPendingBusinessesAsync(role, userId);

                return Ok(ResponseResult.SuccessInfo("âœ… Pending businesses fetched successfully.", result));
            }
            catch (Exception ex)
            {
                return StatusCode(500, ResponseResult.ErrorInfo("âŒ Failed to fetch pending businesses. Please try again later."));
            }
        }


        // âœ… Get business by ID (used for profile completion)

        [HttpGet("{id}")]
        public async Task<IActionResult> GetBusinessById(Guid id)
        {
            try
            {
                var business = await _businessService.GetByIdAsync(id);
                if (business == null)
                    return NotFound(ResponseResult.ErrorInfo("âŒ Business not found."));

                return Ok(business);
            }
            catch (Exception ex)
            {
                return StatusCode(500, ResponseResult.ErrorInfo("ðŸš¨ Failed to fetch business. " + ex.Message));
            }
        }

        [HttpPut("assigned-to/{id}")]
        public async Task<IActionResult> UpdateBusiness(Guid id, [FromBody] Business business)
        {
            if (id != business.Id)
            {
                return BadRequest(new { message = "âŒ ID mismatch." });
            }

            var result = await _businessService.UpdateBusinessAsync(business);
            if (!result.Success)
            {
                return BadRequest(result);
            }

            return Ok(result);
        }


        // ðŸŸ¢ Approve a business
        [HttpPost("approve/{id}")]
        public async Task<IActionResult> Approve(Guid id)
        {
            try
            {
                var result = await _businessService.ApproveBusinessAsync(id);

                if (result.Success)
                {
                    // âœ… Optional Success Logging
                    Log.Information("âœ… Business approved successfully. BusinessId: {BusinessId}", id);
                    return Ok(result);
                }
                else
                {
                    // âœ… Optional Warning Logging
                    Log.Warning("âš ï¸ Business approval failed. BusinessId: {BusinessId} - Message: {Message}", id, result.Message);
                    return BadRequest(result);
                }
            }
            catch (Exception ex)
            {
                // âœ… Proper Error Logging
