        // âœ… Extended logic with condition check (Tag + Source)
        public async Task<CTAFlowStep?> GetChainedStepAsync(
            Guid businessId,
            Guid? nextStepId,
            TrackingLog? trackingLog = null,
            Contact? contact = null)
        {
            if (nextStepId == null)
            {
                Log.Information("â„¹ï¸ No NextStepId provided â€” skipping follow-up.");
                return null;
            }

            try
            {
                var flow = await _context.CTAFlowConfigs
                    .Include(f => f.Steps)
                    .FirstOrDefaultAsync(f =>
                        f.BusinessId == businessId &&
                        f.Steps.Any(s => s.Id == nextStepId));

                if (flow == null)
                {
                    Log.Warning("âš ï¸ No flow found containing NextStepId: {NextStepId} for business: {BusinessId}", nextStepId, businessId);
                    return null;
                }

                var followUpStep = flow.Steps.FirstOrDefault(s => s.Id == nextStepId);

                if (followUpStep == null)
                {
                    Log.Warning("âŒ Step matched in flow but not found in step list: {NextStepId}", nextStepId);
                    return null;
                }

                // âœ… Check RequiredTag / Source match
                if (trackingLog != null)
                {
                    var isMatch = StepMatchingHelper.IsStepMatched(followUpStep, trackingLog, contact);

                    if (!isMatch)
                    {
                        Log.Information("ðŸš« Step {StepId} skipped due to condition mismatch [Tag: {Tag}, Source: {Source}]",
                            followUpStep.Id, followUpStep.RequiredTag, followUpStep.RequiredSource);
                        return null;
                    }
                }

                Log.Information("âœ… Follow-up step found and matched â†’ StepId: {StepId}, Template: {Template}",
                    followUpStep.Id, followUpStep.TemplateToSend);

                return followUpStep;
            }
            catch (Exception ex)
            {
                Log.Error(ex, "âŒ Exception while fetching chained step for NextStepId: {NextStepId}", nextStepId);
                throw;
            }
        }

        // âœ… Optional helper for resolving from TrackingLogId
        public async Task<CTAFlowStep?> GetChainedStepWithContextAsync(
            Guid businessId,
            Guid? nextStepId,
            Guid? trackingLogId)
        {
            var log = await _context.TrackingLogs
                .Include(l => l.Contact)
                    .ThenInclude(c => c.ContactTags)
                        .ThenInclude(ct => ct.Tag)
