        public async Task<ResponseResult> LoginAsync(UserLoginDto dto)
        {
            _logger.LogInformation("üîë Login attempt for email: {Email}", dto.Email);
            var hashedPassword = HashPassword(dto.Password);

            var user = await _userRepo
                .AsQueryable()
                .Where(u => u.Email == dto.Email && u.PasswordHash == hashedPassword && !u.IsDeleted)
                .Include(u => u.Role)
                .FirstOrDefaultAsync();

            if (user == null)
            {
                _logger.LogWarning("‚ùå Login failed: Invalid email or password for {Email}", dto.Email);
                return ResponseResult.ErrorInfo("‚ùå Invalid email or password");
            }

            var roleName = user.Role?.Name?.Trim().ToLower() ?? "unknown";
            var isAdminType = roleName is "admin" or "superadmin" or "partner" or "reseller";

            if (user.BusinessId == null && !isAdminType)
            {
                _logger.LogWarning("‚ùå Login denied for {Email}: No BusinessId and not admin", dto.Email);
                return ResponseResult.ErrorInfo("‚ùå Your account approval is pending. Please contact your administrator or support.");
            }

            Business? business = null;
            if (user.BusinessId != null)
            {
                business = await _businessService.Query()
                    .Include(b => b.BusinessPlanInfo)
                    .FirstOrDefaultAsync(b => b.Id == user.BusinessId.Value);

                if (business == null)
                    return ResponseResult.ErrorInfo("‚ùå Associated business not found.");

                if (business.Status == Business.StatusType.Pending)
                    return ResponseResult.ErrorInfo("‚è≥ Your business is under review. Please wait for approval.");
            }

            var permissions = await _accessControlService.GetPermissionsAsync(user.Id);

            string planName;
            string companyName;
            string businessId = user.BusinessId?.ToString() ?? "";

            if (isAdminType)
            {
                planName = roleName;        // admin types treated as plan in UI
                companyName = "xByte Admin";
                businessId = "";
            }
            else
            {
                planName = business?.BusinessPlanInfo?.Plan.ToString() ?? "basic";
                companyName = business?.CompanyName ?? "";
            }

            // ‚úÖ Generate JWT (includes role/plan/biz + ClaimTypes.Role)
            var token = _jwtTokenService.GenerateToken(
                user.Id.ToString(),
                roleName,
                user.Name ?? "",
                user.Email ?? "",
                user.Status ?? "unknown",
                businessId,
                companyName,
                planName,
                permissions ?? new List<string>()
            );

            // ‚ùå NO cookie writes in Bearer mode

            var userDto = new UserDto
            {
                Id = user.Id,
                Name = user.Name,
                Email = user.Email,
                Role = roleName,
                Status = user.Status,
                CreatedAt = user.CreatedAt,
                BusinessId = string.IsNullOrEmpty(businessId) ? Guid.Empty : Guid.Parse(businessId),
                CompanyName = companyName,
                Plan = planName,
                AccessToken = null
            };

            _logger.LogInformation("‚úÖ Login successful for {Email}, Role: {Role}, Plan: {Plan}", dto.Email, roleName, planName);

            return new ResponseResult
            {
                Success = true,
                Message = "‚úÖ Login successful",
                Data = userDto,
                Token = token
            };
        }

        public async Task<ResponseResult> SignupAsync(SignupBusinessDto dto)
        {
            _logger.LogInformation("üü¢ Signup attempt: {Email}", dto.Email);
            var result = await _businessService.SignupBusinessAsync(dto);

            if (!result.Success)
            {
                _logger.LogWarning("‚ùå Signup failed for {Email}: {Msg}", dto.Email, result.Message);
                return ResponseResult.ErrorInfo(result.Message);
            }

            var business = await _businessService.GetBusinessByEmailAsync(dto.Email);

            if (business == null)
            {
                _logger.LogError("‚ùå Signup succeeded but business retrieval failed for {Email}", dto.Email);
                return ResponseResult.ErrorInfo("‚ùå Signup succeeded but business retrieval failed.");
            }

            try
            {
                // üÜï Set BusinessAssignedTo if available
                if (dto.CreatedByPartnerId.HasValue && business.CreatedByPartnerId == null)
                {
                    business.CreatedByPartnerId = dto.CreatedByPartnerId;
                    await _businessService.UpdateBusinessAsync(business);
                    _logger.LogInformation("‚úÖ Partner assigned during signup: {PartnerId} for Business: {BusinessId}", dto.CreatedByPartnerId, business.Id);
                }
            }
            catch (Exception ex)
            {
                _logger.LogError(ex, "‚ùå Signup succeeded but assigning partner failed for {Email}", dto.Email);
                return ResponseResult.ErrorInfo("‚ùå Signup succeeded but assigning partner failed: " + ex.Message);
            }

            _logger.LogInformation("‚úÖ Signup successful for {Email} (BusinessId: {BusinessId})", dto.Email, business.Id);
            return ResponseResult.SuccessInfo("‚úÖ Signup successful. Pending approval.", new { BusinessId = business.Id });
        }

        // üîÑ Refresh JWT Token (and Rotate)
        public async Task<ResponseResult> RefreshTokenAsync(string refreshToken)
        {
            _logger.LogInformation("üîÑ RefreshToken attempt");

            var user = await _userRepo
                .AsQueryable()
                .Include(u => u.Role)
                .Include(u => u.Business)
                    .ThenInclude(b => b.BusinessPlanInfo)
                .FirstOrDefaultAsync(u => u.RefreshToken == refreshToken && u.RefreshTokenExpiry > DateTime.UtcNow);

            if (user == null)
            {
                _logger.LogWarning("‚ùå Invalid or expired refresh token.");
                return ResponseResult.ErrorInfo("‚ùå Invalid or expired refresh token.");
            }

            var permissions = await _accessControlService.GetPermissionsAsync(user.Id);
            var roleName = user.Role?.Name?.Trim().ToLower() ?? "unknown";
            var isAdminType = roleName == "superadmin" || roleName == "partner" || roleName == "reseller";

            string planName;
            string companyName;
            string businessId = user.BusinessId?.ToString() ?? "";

            if (isAdminType)
            {
                planName = roleName;
                companyName = "xByte Admin";
                businessId = "";
            }
            else
            {
                planName = user.Business?.BusinessPlanInfo?.Plan.ToString() ?? "";
                companyName = user.Business?.CompanyName ?? "";
            }

            var claims = new List<Claim>
            {
                new Claim(JwtRegisteredClaimNames.Sub, user.Id.ToString()),
                new Claim(JwtRegisteredClaimNames.Email, user.Email ?? ""),
                new Claim("name", user.Name ?? ""),
                new Claim(ClaimTypes.Role, roleName),
                new Claim("role", roleName),
                new Claim("status", user.Status ?? "unknown"),
                new Claim("plan", planName ?? ""),
                new Claim("businessId", businessId),
                new Claim("companyName", companyName ?? "")
            };

            if (permissions?.Any() == true)
            {
                claims.AddRange(permissions.Select(p => new Claim("perm", p)));
            }

            var token = _jwtTokenService.GenerateToken(claims);

            // üîÅ Rotate refresh token
            var newRefreshToken = Guid.NewGuid().ToString("N");
            user.RefreshToken = newRefreshToken;
            user.RefreshTokenExpiry = DateTime.UtcNow.AddDays(30);
            _userRepo.Update(user);

            _logger.LogInformation("üîÑ Token refreshed for user {UserId}, role {Role}", user.Id, roleName);

            return ResponseResult.SuccessInfo("üîÑ Token refreshed", new
            {
                accessToken = token,
                refreshToken = newRefreshToken
            });
        }

        // üîÅ Resend confirmation
        public async Task<ResponseResult> ResendConfirmationAsync(ResendConfirmationDto dto)
        {
            _logger.LogInformation("üîÅ Resend confirmation attempt for {Email}", dto.Email);
            var business = await _businessService.GetBusinessByEmailAsync(dto.Email);
            if (business == null)
            {
                _logger.LogWarning("‚ùå Resend confirmation failed: No business for {Email}", dto.Email);
                return ResponseResult.ErrorInfo("‚ùå No business registered with this email");
            }

            _logger.LogInformation("‚úÖ Resend confirmation request processed for {Email}", dto.Email);
            return ResponseResult.SuccessInfo("üì® Confirmation request resent.");
        }
        public async Task<FeatureAccessDto> GetFeatureAccessForUserAsync(ClaimsPrincipal user)
        {
            var role = user.FindFirstValue("role")?.ToLower();
            var dto = new FeatureAccessDto();

            if (role == "superadmin")
            {
                // Grant all known frontend features
                dto.Features = await _dbContext.FeatureAccess
                    .Select(f => f.FeatureName)
                    .Distinct()
                    .ToDictionaryAsync(name => name, name => true);

                return dto;
            }

            var plan = user.FindFirstValue("plan")?.ToLower();
            var businessIdStr = user.FindFirstValue("businessId");

            if (!Guid.TryParse(businessIdStr, out var businessId))
                return dto;

            // Plan-level or per-business override
            var features = await _dbContext.FeatureAccess
                .Where(f => f.BusinessId == businessId || f.Plan.ToLower() == plan)
                .ToListAsync();

            foreach (var feature in features)
            {
                dto.Features[feature.FeatureName] = feature.IsEnabled;
            }

            return dto;
        }

        // üîí Reset password
        public async Task<ResponseResult> ResetPasswordAsync(ResetPasswordDto dto)
        {
            _logger.LogInformation("üîí Reset password attempt for {Email}", dto.Email);
            var user = await _userRepo.FirstOrDefaultAsync(u => u.Email == dto.Email);
            if (user == null)
            {
                _logger.LogWarning("‚ùå Reset password failed: No user for {Email}", dto.Email);
                return ResponseResult.ErrorInfo("‚ùå No user found with this email");
            }

            user.PasswordHash = HashPassword(dto.NewPassword);
            _userRepo.Update(user);

            _logger.LogInformation("‚úÖ Password reset successfully for {Email}", dto.Email);
            return ResponseResult.SuccessInfo("‚úÖ Password reset successfully");
        }

        // Utility: Hash password using SHA256
        private string HashPassword(string password)
        {
            using var sha = SHA256.Create();
            var bytes = Encoding.UTF8.GetBytes(password);
            var hash = sha.ComputeHash(bytes);
            return Convert.ToBase64String(hash);
        }
    }
