            var contact = await _contactService.GetContactByIdAsync(businessId, id);
            if (contact == null)
                return NotFound(ResponseResult.ErrorInfo("Contact not found."));
            return Ok(ResponseResult.SuccessInfo("Contact loaded.", contact));
        }

