            TotalReached == 0 ? 0 : Math.Round((double)ClickedNext / TotalReached * 100, 2);

        // ‚úÖ Optional: percentage of users who dropped off
        public double DropOffRate =>
            TotalReached == 0 ? 0 : Math.Round((double)DropOff / TotalReached * 100, 2);
    }
