        public async Task<ResponseResult> LoginAsync(UserLoginDto dto)
        {
            _logger.LogInformation("üîë Login attempt for email: {Email}", dto.Email);
            var hashedPassword = HashPassword(dto.Password);

            var user = await _userRepo
                .AsQueryable()
                .Where(u => u.Email == dto.Email && u.PasswordHash == hashedPassword && !u.IsDeleted)
                .Include(u => u.Role)
                .FirstOrDefaultAsync();

            if (user == null)
            {
                _logger.LogWarning("‚ùå Login failed: Invalid email or password for {Email}", dto.Email);
                return ResponseResult.ErrorInfo("‚ùå Invalid email or password");
            }

            var roleName = user.Role?.Name?.Trim().ToLower() ?? "unknown";
            var isAdminType = roleName is "admin" or "superadmin" or "partner" or "reseller";

            if (user.BusinessId == null && !isAdminType)
            {
                _logger.LogWarning("‚ùå Login denied for {Email}: No BusinessId and not admin", dto.Email);
                return ResponseResult.ErrorInfo("‚ùå Your account approval is pending. Please contact your administrator or support.");
            }

            Business? business = null;
            Guid? planId = null;
            string companyName = string.Empty;
            string businessId = user.BusinessId?.ToString() ?? string.Empty;

            if (user.BusinessId != null)
            {
                business = await _businessService.Query()
                    .Include(b => b.Plan)
                    .FirstOrDefaultAsync(b => b.Id == user.BusinessId.Value);

                if (business == null)
                    return ResponseResult.ErrorInfo("‚ùå Associated business not found.");

                if (business.Status == Business.StatusType.Pending)
                    return ResponseResult.ErrorInfo("‚è≥ Your business is under review. Please wait for approval.");

                if (!business.PlanId.HasValue)
                    return ResponseResult.ErrorInfo("‚ùå No plan assigned to this business.");

                planId = business.PlanId;
                companyName = business.CompanyName ?? string.Empty;
            }

            if (isAdminType)
            {
                // Admins don‚Äôt get plan restrictions
                companyName = "xByte Admin";
                businessId = string.Empty;
                planId = null;
            }

            // üî• Compute EFFECTIVE permissions (plan ‚à© role) and derive features
            var (permCodes, featureKeys) = isAdminType
                ? (await GetAllActivePermissions(), new List<string> { "Dashboard", "Messaging", "CRM", "Campaigns", "Catalog", "AdminPanel" })
                : await GetEffectivePermissionsAndFeaturesAsync(user.Id);

            // üé´ Generate JWT (now includes permissions, features, plan_id)
            var token = _jwtTokenService.GenerateToken(
                userId: user.Id.ToString(),
                role: roleName,
                userName: user.Name ?? string.Empty,
                email: user.Email ?? string.Empty,
                status: user.Status ?? "unknown",
                businessId: businessId,
                companyName: companyName,
                permissions: permCodes ?? new List<string>(),
                planId: planId?.ToString() ?? string.Empty,
                features: featureKeys,
                hasAllAccess: isAdminType
            );
            try
            {
                var jwt = new JwtSecurityTokenHandler().ReadJwtToken(token);
                var pid = jwt.Claims.FirstOrDefault(c => c.Type == "plan_id")?.Value;
                _logger.LogInformation("üîé JWT includes plan_id: {PlanId}", pid ?? "<null>");
            }
            catch { /* ignore */ }
            var userDto = new UserDto
            {
                Id = user.Id,
                Name = user.Name,
                Email = user.Email,
                Role = roleName,
                Status = user.Status,
                CreatedAt = user.CreatedAt,
                BusinessId = string.IsNullOrEmpty(businessId) ? Guid.Empty : Guid.Parse(businessId),
                CompanyName = companyName,
                PlanId = planId,
                AccessToken = null
            };

            _logger.LogInformation(
                "‚úÖ Login successful for {Email}, Role: {Role}, PlanId: {PlanId}",
                dto.Email, roleName, planId
            );

            return new ResponseResult
            {
                Success = true,
                Message = "‚úÖ Login successful",
                Data = userDto,
                Token = token
            };
        }
        public async Task<ResponseResult> SignupAsync(SignupBusinessDto dto)
        {
            _logger.LogInformation("üü¢ Signup attempt: {Email}", dto.Email);
            var result = await _businessService.SignupBusinessAsync(dto);

            if (!result.Success)
            {
                _logger.LogWarning("‚ùå Signup failed for {Email}: {Msg}", dto.Email, result.Message);
                return ResponseResult.ErrorInfo(result.Message);
            }

            var business = await _businessService.GetBusinessByEmailAsync(dto.Email);

            if (business == null)
            {
                _logger.LogError("‚ùå Signup succeeded but business retrieval failed for {Email}", dto.Email);
                return ResponseResult.ErrorInfo("‚ùå Signup succeeded but business retrieval failed.");
            }

            try
            {
                // üÜï Set BusinessAssignedTo if available
                if (dto.CreatedByPartnerId.HasValue && business.CreatedByPartnerId == null)
                {
                    business.CreatedByPartnerId = dto.CreatedByPartnerId;
                    await _businessService.UpdateBusinessAsync(business);
                    _logger.LogInformation("‚úÖ Partner assigned during signup: {PartnerId} for Business: {BusinessId}", dto.CreatedByPartnerId, business.Id);
                }
            }
            catch (Exception ex)
            {
                _logger.LogError(ex, "‚ùå Signup succeeded but assigning partner failed for {Email}", dto.Email);
                return ResponseResult.ErrorInfo("‚ùå Signup succeeded but assigning partner failed: " + ex.Message);
            }

            _logger.LogInformation("‚úÖ Signup successful for {Email} (BusinessId: {BusinessId})", dto.Email, business.Id);
            return ResponseResult.SuccessInfo("‚úÖ Signup successful. Pending approval.", new { BusinessId = business.Id });
        }

