        public async Task<ResponseResult> ResetPasswordAsync(ResetPasswordDto dto)
        {
            _logger.LogInformation("üîí Reset password attempt for {Email}", dto.Email);
            var user = await _userRepo.FirstOrDefaultAsync(u => u.Email == dto.Email);
            if (user == null)
            {
                _logger.LogWarning("‚ùå Reset password failed: No user for {Email}", dto.Email);
                return ResponseResult.ErrorInfo("‚ùå No user found with this email");
            }

            user.PasswordHash = HashPassword(dto.NewPassword);
            _userRepo.Update(user);

            _logger.LogInformation("‚úÖ Password reset successfully for {Email}", dto.Email);
            return ResponseResult.SuccessInfo("‚úÖ Password reset successfully");
        }

