        public async Task<List<string>> GetPermissionsByPlanIdAsync(Guid? planId)
        {
            if (!planId.HasValue)
                return new List<string>();

            return await _context.PlanPermissions
                .Where(pp => pp.PlanId == planId.Value && pp.IsActive)
                .Select(pp => pp.Permission.Code)
                .ToListAsync();
        }


    }
