                        Log.Information("‚ÑπÔ∏è Aligning selected template '{Sel}' to flow entry '{Entry}'.",
                            selectedTemplateName, entryStep.TemplateToSend);
                        selectedTemplateName = entryStep.TemplateToSend;
                    }
                }
            }
