using Microsoft.EntityFrameworkCore;
using Newtonsoft.Json;
using xbytechat.api.AuthModule.Models;
using xbytechat.api;
using xbytechat.api.WhatsAppSettings.Abstractions;
using xbytechat.api.WhatsAppSettings.Providers;
using xbytechat_api.WhatsAppSettings.Models;

public record TemplateSyncResult(int Added, int Updated, int Skipped, DateTime SyncedAt);

public interface ITemplateSyncService
{
    Task<TemplateSyncResult> SyncBusinessTemplatesAsync(Guid businessId, bool force = false, CancellationToken ct = default);
}

public sealed class TemplateSyncService : ITemplateSyncService
{
    private readonly AppDbContext _db;
    private readonly MetaTemplateCatalogProvider _meta;
    private readonly PinnacleTemplateCatalogProvider _pinnacle;
    private readonly ILogger<TemplateSyncService> _log;

    private static readonly TimeSpan TTL = TimeSpan.FromHours(12);

    public TemplateSyncService(AppDbContext db,
        MetaTemplateCatalogProvider meta,
        PinnacleTemplateCatalogProvider pinnacle,
        ILogger<TemplateSyncService> log)
    { _db = db; _meta = meta; _pinnacle = pinnacle; _log = log; }

    public async Task<TemplateSyncResult> SyncBusinessTemplatesAsync(Guid businessId, bool force = false, CancellationToken ct = default)
    {
        var setting = await _db.WhatsAppSettings.FirstOrDefaultAsync(x => x.BusinessId == businessId && x.IsActive, ct)
                      ?? throw new InvalidOperationException("Active WhatsApp settings not found.");

        var now = DateTime.UtcNow;

        // TTL short-circuit
        if (!force)
        {
            var recent = await _db.WhatsAppTemplates
                .Where(t => t.BusinessId == businessId)
                .OrderByDescending(t => t.LastSyncedAt)
                .Select(t => t.LastSyncedAt)
                .FirstOrDefaultAsync(ct);

            if (recent != default && now - recent < TTL)
            {
                _log.LogInformation(" Skipping sync for {BusinessId}; TTL not expired.", businessId);
                return new TemplateSyncResult(0, 0, 0, recent);
            }
        }

        var providerKey = (setting.Provider ?? "meta_cloud").Trim().ToLowerInvariant();
        IReadOnlyList<TemplateCatalogItem> incoming = providerKey switch
        {
            "meta_cloud" => await _meta.ListAsync(setting, ct),
            "pinnacle" => await _pinnacle.ListAsync(setting, ct),
            _ => Array.Empty<TemplateCatalogItem>()
        };

        int added = 0, updated = 0, skipped = 0;

        foreach (var it in incoming)
        {
            var existing = await _db.WhatsAppTemplates.FirstOrDefaultAsync(t =>
                t.BusinessId == businessId &&
                t.Provider == providerKey &&
                t.Name == it.Name &&
                t.Language == it.Language, ct);

            var buttonsJson = JsonConvert.SerializeObject(it.Buttons);

            if (existing == null)
            {
                await _db.WhatsAppTemplates.AddAsync(new WhatsAppTemplate
                {
                    BusinessId = businessId,
                    Provider = providerKey,
                    ExternalId = it.ExternalId,
                    Name = it.Name,
                    Language = it.Language,
                    Status = string.IsNullOrWhiteSpace(it.Status) ? "APPROVED" : it.Status,
                    Category = it.Category,
                    Body = it.Body ?? "",
                    HasImageHeader = it.HasImageHeader,
                    PlaceholderCount = it.PlaceholderCount,
                    ButtonsJson = buttonsJson,
                    RawJson = it.RawJson,
                    LastSyncedAt = now,
                    CreatedAt = now,
                    UpdatedAt = now,
                    IsActive = true
                }, ct);
                added++;
            }
            else
            {
                existing.ExternalId = it.ExternalId ?? existing.ExternalId;
                existing.Status = string.IsNullOrWhiteSpace(it.Status) ? existing.Status : it.Status;
                existing.Category = it.Category ?? existing.Category;
                existing.Body = it.Body ?? existing.Body;
                existing.HasImageHeader = it.HasImageHeader;
                existing.PlaceholderCount = it.PlaceholderCount;
                existing.ButtonsJson = buttonsJson;
                existing.RawJson = it.RawJson ?? existing.RawJson;
                existing.LastSyncedAt = now;
                existing.UpdatedAt = now;
                existing.IsActive = true;
                updated++;
            }
        }

        await _db.SaveChangesAsync(ct);

        return new TemplateSyncResult(added, updated, skipped, now);
    }
}

//  xbytechat_api/WhatsAppSettings/Services/WhatsAppSettingsService.cs
using Microsoft.EntityFrameworkCore;
using System;
using System.Net.Http;
using System.Net.Http.Headers;
using System.Text;
using System.Text.Json;
using System.Threading.Tasks;
using xbytechat.api;
using xbytechat_api.WhatsAppSettings.DTOs;
using xbytechat_api.WhatsAppSettings.Models;

namespace xbytechat_api.WhatsAppSettings.Services
{
    public class WhatsAppSettingsService : IWhatsAppSettingsService
    {
        private readonly AppDbContext _dbContext;
        private readonly HttpClient _http;                    // kept for any other callers you may have
        private readonly IHttpClientFactory _httpClientFactory;

        public WhatsAppSettingsService(
            AppDbContext dbContext,
            HttpClient http,
            IHttpClientFactory httpClientFactory)
        {
            _dbContext = dbContext;
            _http = http;
            _httpClientFactory = httpClientFactory;
        }

        public async Task SaveOrUpdateSettingAsync(SaveWhatsAppSettingDto dto)
        {
            if (dto.BusinessId == Guid.Empty)
                throw new ArgumentException("Invalid BusinessId provided.", nameof(dto.BusinessId));

            // Normalize provider (store lower-case for consistency)
            var provider = (dto.Provider ?? "pinnacle").Trim();
            if (string.IsNullOrWhiteSpace(provider))
                provider = "pinnacle";
            var providerNorm = provider.ToLowerInvariant();

            // Look up by BusinessId + Provider (case-insensitive)
            var existing = await _dbContext.WhatsAppSettings
                .FirstOrDefaultAsync(x => x.BusinessId == dto.BusinessId && x.Provider.ToLower() == providerNorm);

            if (existing != null)
            {
                // keep provider normalized
                existing.Provider = providerNorm;

                // Only overwrite when incoming value is non-empty (avoid wiping secrets/tokens accidentally)
                if (!string.IsNullOrWhiteSpace(dto.ApiUrl)) existing.ApiUrl = dto.ApiUrl.Trim();
                if (!string.IsNullOrWhiteSpace(dto.ApiKey)) existing.ApiKey = dto.ApiKey.Trim();
                if (!string.IsNullOrWhiteSpace(dto.ApiToken)) existing.ApiToken = dto.ApiToken!.Trim();

                if (!string.IsNullOrWhiteSpace(dto.PhoneNumberId)) existing.PhoneNumberId = dto.PhoneNumberId!.Trim();
                if (!string.IsNullOrWhiteSpace(dto.WhatsAppBusinessNumber)) existing.WhatsAppBusinessNumber = dto.WhatsAppBusinessNumber!.Trim();
                if (!string.IsNullOrWhiteSpace(dto.SenderDisplayName)) existing.SenderDisplayName = dto.SenderDisplayName!.Trim();
                if (!string.IsNullOrWhiteSpace(dto.WabaId)) existing.WabaId = dto.WabaId!.Trim();

                //  Webhook auth fields (optional)
                if (!string.IsNullOrWhiteSpace(dto.WebhookSecret)) existing.WebhookSecret = dto.WebhookSecret!.Trim();
                if (!string.IsNullOrWhiteSpace(dto.WebhookVerifyToken)) existing.WebhookVerifyToken = dto.WebhookVerifyToken!.Trim();

                //  NEW: provider callback URL (optional)
                if (!string.IsNullOrWhiteSpace(dto.WebhookCallbackUrl)) existing.WebhookCallbackUrl = dto.WebhookCallbackUrl!.Trim();

                existing.IsActive = dto.IsActive;
                existing.UpdatedAt = DateTime.UtcNow;
            }
            else
            {
                var newSetting = new WhatsAppSettingEntity
                {
                    Id = Guid.NewGuid(),
                    BusinessId = dto.BusinessId,
                    Provider = providerNorm,
                    ApiUrl = (dto.ApiUrl ?? string.Empty).Trim(),
                    ApiKey = string.IsNullOrWhiteSpace(dto.ApiKey) ? null : dto.ApiKey!.Trim(),
                    ApiToken = string.IsNullOrWhiteSpace(dto.ApiToken) ? null : dto.ApiToken!.Trim(),
                    PhoneNumberId = string.IsNullOrWhiteSpace(dto.PhoneNumberId) ? null : dto.PhoneNumberId!.Trim(),
                    WhatsAppBusinessNumber = string.IsNullOrWhiteSpace(dto.WhatsAppBusinessNumber) ? null : dto.WhatsAppBusinessNumber!.Trim(),
                    SenderDisplayName = string.IsNullOrWhiteSpace(dto.SenderDisplayName) ? null : dto.SenderDisplayName!.Trim(),
                    WabaId = string.IsNullOrWhiteSpace(dto.WabaId) ? null : dto.WabaId!.Trim(),

                    //  Webhook auth fields (optional)
                    WebhookSecret = string.IsNullOrWhiteSpace(dto.WebhookSecret) ? null : dto.WebhookSecret!.Trim(),
                    WebhookVerifyToken = string.IsNullOrWhiteSpace(dto.WebhookVerifyToken) ? null : dto.WebhookVerifyToken!.Trim(),

                    //  NEW: provider callback URL (optional)
                    WebhookCallbackUrl = string.IsNullOrWhiteSpace(dto.WebhookCallbackUrl) ? null : dto.WebhookCallbackUrl!.Trim(),

                    IsActive = dto.IsActive,
                    CreatedAt = DateTime.UtcNow
                };

                await _dbContext.WhatsAppSettings.AddAsync(newSetting);
            }

            await _dbContext.SaveChangesAsync();
        }

        public async Task<WhatsAppSettingEntity?> GetSettingsByBusinessIdAsync(Guid businessId)
        {
            return await _dbContext.WhatsAppSettings
                .FirstOrDefaultAsync(x => x.BusinessId == businessId && x.IsActive);
        }

        public async Task<bool> DeleteSettingsAsync(Guid businessId)
        {
            var setting = await _dbContext.WhatsAppSettings
                .FirstOrDefaultAsync(x => x.BusinessId == businessId);

            if (setting == null) return false;

            _dbContext.WhatsAppSettings.Remove(setting);
            await _dbContext.SaveChangesAsync();
            return true;
        }

        /// <summary>
        /// Provider-aware test connection. Returns a short message (/ ).
        /// The controller may convert non- messages to 400, etc.
        /// </summary>
        public async Task<string> TestConnectionAsync(SaveWhatsAppSettingDto dto)
        {
            if (string.IsNullOrWhiteSpace(dto.Provider))
                throw new ArgumentException("Provider is required.");

            // normalize provider and baseUrl
            var provider = dto.Provider.Trim();
            var lower = provider.ToLowerInvariant();
            var baseUrl = (dto.ApiUrl ?? string.Empty).Trim().TrimEnd('/');

            if (string.IsNullOrWhiteSpace(baseUrl))
                throw new ArgumentException("ApiUrl is required.");

            var http = _httpClientFactory.CreateClient();

            // ----- Meta Cloud -----
            if (lower == "meta_cloud")
            {
                if (string.IsNullOrWhiteSpace(dto.ApiToken))
                    throw new ArgumentException("ApiToken is required for Meta Cloud.");
                if (string.IsNullOrWhiteSpace(dto.PhoneNumberId))
                    throw new ArgumentException("PhoneNumberId is required for Meta Cloud.");

                http.DefaultRequestHeaders.Authorization =
                    new AuthenticationHeaderValue("Bearer", dto.ApiToken);

                var url = $"{baseUrl}/{dto.PhoneNumberId}";
                var res = await http.GetAsync(url);
                var body = await res.Content.ReadAsStringAsync();

                if (!res.IsSuccessStatusCode)
                    return $" Meta Cloud test failed ({(int)res.StatusCode}). Body: {body}";

                return " Meta Cloud token & phone number ID are valid.";
            }

            // ----- Pinnacle (formerly Pinbot) -----
            if (lower == "pinnacle")
            {
                if (string.IsNullOrWhiteSpace(dto.ApiKey))
                    return " API Key is required for Pinnacle.";

                // Pinnacle requires either phone number id OR WABA id in the path
                var pathId =
                    !string.IsNullOrWhiteSpace(dto.PhoneNumberId) ? dto.PhoneNumberId!.Trim() :
                    !string.IsNullOrWhiteSpace(dto.WabaId) ? dto.WabaId!.Trim() :
                    null;

                if (string.IsNullOrWhiteSpace(pathId))
                    return " Provide PhoneNumberId or WabaId for Pinnacle.";

                if (string.IsNullOrWhiteSpace(dto.WhatsAppBusinessNumber))
                    return " WhatsApp Business Number is required for Pinnacle test.";

                var url = $"{baseUrl}/{pathId}/messages";
                var payload = new
                {
                    to = dto.WhatsAppBusinessNumber,
                    type = "text",
                    text = new { body = "Test message" },
                    messaging_product = "whatsapp"
                };

                using var req = new HttpRequestMessage(HttpMethod.Post, url);
                req.Headers.TryAddWithoutValidation("apikey", dto.ApiKey);
                req.Content = new StringContent(JsonSerializer.Serialize(payload), Encoding.UTF8, "application/json");

                var res = await http.SendAsync(req);
                var body = await res.Content.ReadAsStringAsync();

                if (!res.IsSuccessStatusCode)
                {
                    if ((int)res.StatusCode == 401 || (int)res.StatusCode == 403)
                        return $" Pinnacle rejected the API key for id '{pathId}'. Verify the key and id. Body: {body}";

                    return $" Pinnacle test failed ({(int)res.StatusCode}). Body: {body}";
                }

                return " Pinnacle API key and endpoint are reachable.";
            }

            return $" Unsupported provider: {dto.Provider}";
        }

        public async Task<string?> GetSenderNumberAsync(Guid businessId)
        {
            var setting = await _dbContext.WhatsAppSettings
                .AsNoTracking()
                .FirstOrDefaultAsync(x => x.BusinessId == businessId && x.IsActive);

            return setting?.WhatsAppBusinessNumber;
        }

        public async Task<string> GetCallbackUrlAsync(Guid businessId, string appBaseUrl)
        {
            var s = await _dbContext.WhatsAppSettings
                .AsNoTracking()
                .FirstOrDefaultAsync(x => x.BusinessId == businessId && x.IsActive);

            if (!string.IsNullOrWhiteSpace(s?.WebhookCallbackUrl))
                return s!.WebhookCallbackUrl!;

            return $"{appBaseUrl.TrimEnd('/')}/api/webhookcallback";
        }

    }
}

using Microsoft.EntityFrameworkCore;
using Newtonsoft.Json;
using Newtonsoft.Json.Linq;
using System.Net.Http.Headers;
using System.Security.Claims;
using System.Text.RegularExpressions;
using xbytechat.api;
using xbytechat.api.Shared;
using xbytechat.api.WhatsAppSettings.DTOs;

namespace xbytechat_api.WhatsAppSettings.Services
{

    public class WhatsAppTemplateFetcherService : IWhatsAppTemplateFetcherService
    {
        private readonly AppDbContext _dbContext;
        private readonly HttpClient _httpClient;
        private readonly ILogger<WhatsAppTemplateFetcherService> _logger;
        private readonly IHttpContextAccessor _httpContextAccessor;
        public WhatsAppTemplateFetcherService(AppDbContext dbContext, HttpClient httpClient, ILogger<WhatsAppTemplateFetcherService> logger, IHttpContextAccessor httpContextAccessor)
        {
            _dbContext = dbContext;
            _httpClient = httpClient;
            _logger = logger;
            _httpContextAccessor = httpContextAccessor;
        }
        public async Task<List<TemplateMetadataDto>> FetchTemplatesAsync(Guid businessId)
        {
            var templates = new List<TemplateMetadataDto>();

            var setting = await _dbContext.WhatsAppSettings
                .FirstOrDefaultAsync(x => x.BusinessId == businessId && x.IsActive);

            if (setting == null)
            {
                _logger.LogWarning("WhatsApp Settings not found for BusinessId: {BusinessId}", businessId);
                return templates;
            }

            var provider = (setting.Provider ?? "").Trim().ToLowerInvariant();
            var baseUrl = setting.ApiUrl?.TrimEnd('/') ?? "";

            try
            {
                if (provider == "meta_cloud")
                {
                    if (string.IsNullOrWhiteSpace(setting.ApiToken) || string.IsNullOrWhiteSpace(setting.WabaId))
                    {
                        _logger.LogWarning("Missing API Token or WABA ID for BusinessId: {BusinessId}", businessId);
                        return templates;
                    }

                    var nextUrl = $"{(string.IsNullOrWhiteSpace(baseUrl) ? "https://graph.facebook.com/v22.0" : baseUrl)}/{setting.WabaId}/message_templates?limit=100";
                    _httpClient.DefaultRequestHeaders.Authorization = new AuthenticationHeaderValue("Bearer", setting.ApiToken);

                    while (!string.IsNullOrWhiteSpace(nextUrl))
                    {
                        var response = await _httpClient.GetAsync(nextUrl);
                        var json = await response.Content.ReadAsStringAsync();
                        _logger.LogInformation(" Meta Template API Raw JSON for {BusinessId}:\n{Json}", setting.BusinessId, json);

                        if (!response.IsSuccessStatusCode)
                        {
                            _logger.LogError(" Failed to fetch templates from Meta: {Response}", json);
                            break;
                        }

                        dynamic parsed = JsonConvert.DeserializeObject(json);
                        templates.AddRange(ParseTemplatesFromMetaLikePayload(parsed));
                        nextUrl = parsed?.paging?.next?.ToString();
                    }

                    return templates;
                }
                else if (provider == "pinnacle")
                {
                    if (string.IsNullOrWhiteSpace(setting.ApiKey))
                    {
                        _logger.LogWarning("Pinnacle API key missing for BusinessId: {BusinessId}", businessId);
                        return templates;
                    }

                    // Pinnacle typically accepts either WABA ID or PhoneNumberId. Prefer WABA.
                    var pathId = !string.IsNullOrWhiteSpace(setting.WabaId)
                        ? setting.WabaId!.Trim()
                        : setting.PhoneNumberId?.Trim();

                    if (string.IsNullOrWhiteSpace(pathId))
                    {
                        _logger.LogWarning("Pinnacle path id missing (WabaId/PhoneNumberId) for BusinessId: {BusinessId}", businessId);
                        return templates;
                    }

                    var nextUrl = $"{(string.IsNullOrWhiteSpace(baseUrl) ? "https://partnersv1.pinbot.ai/v3" : baseUrl)}/{pathId}/message_templates?limit=100";

                    // IMPORTANT: Pinnacle needs apikey header
                    _httpClient.DefaultRequestHeaders.Remove("apikey");
                    _httpClient.DefaultRequestHeaders.TryAddWithoutValidation("apikey", setting.ApiKey);

                    while (!string.IsNullOrWhiteSpace(nextUrl))
                    {
                        var response = await _httpClient.GetAsync(nextUrl);
                        var json = await response.Content.ReadAsStringAsync();
                        _logger.LogInformation(" Pinnacle Template API Raw JSON for {BusinessId}:\n{Json}", setting.BusinessId, json);

                        if (!response.IsSuccessStatusCode)
                        {
                            _logger.LogError(" Failed to fetch templates from Pinnacle: {Response}", json);
                            break;
                        }

                        // Try to support both "data": [...] and "templates": [...] styles
                        dynamic parsed = JsonConvert.DeserializeObject(json);
                        templates.AddRange(ParseTemplatesFromMetaLikePayload(parsed)); // many BSPs mirror Meta's shape
                        nextUrl = parsed?.paging?.next?.ToString(); // if their API paginates similarly
                                                                    // If no paging in Pinnacle, set nextUrl = null to exit loop
                        if (nextUrl == null) break;
                    }

                    return templates;
                }
                else
                {
                    _logger.LogInformation("Provider {Provider} does not support listing via API in this build.", provider);
                    return templates;
                }
            }
            catch (Exception ex)
            {
                _logger.LogError(ex, "Exception while fetching WhatsApp templates for provider {Provider}.", provider);
                return templates;
            }
        }

        private static IEnumerable<TemplateMetadataDto> ParseTemplatesFromMetaLikePayload(dynamic parsed)
        {
            var list = new List<TemplateMetadataDto>();
            if (parsed == null) return list;

            // Prefer parsed.data; fall back to parsed.templates
            var collection = parsed.data ?? parsed.templates;
            if (collection == null) return list;

            foreach (var tpl in collection)
            {
                string name = tpl.name?.ToString() ?? "";
                string language = tpl.language?.ToString() ?? "en_US";
                string body = "";
                bool hasImageHeader = false;
                var buttons = new List<ButtonMetadataDto>();

                // components may be null for some BSPs
                var components = tpl.components;
                if (components != null)
                {
                    foreach (var component in components)
                    {
                        string type = component.type?.ToString()?.ToUpperInvariant();

                        if (type == "BODY")
                            body = component.text?.ToString() ?? "";

                        if (type == "HEADER" && (component.format?.ToString()?.ToUpperInvariant() == "IMAGE"))
                            hasImageHeader = true;

                        if (type == "BUTTONS" && component.buttons != null)
                        {
                            foreach (var button in component.buttons)
                            {
                                try
                                {
                                    string btnType = button.type?.ToString()?.ToUpperInvariant() ?? "";
                                    string text = button.text?.ToString() ?? "";
                                    int index = buttons.Count;

                                    string subType = btnType switch
                                    {
                                        "URL" => "url",
                                        "PHONE_NUMBER" => "voice_call",
                                        "QUICK_REPLY" => "quick_reply",
                                        "COPY_CODE" => "copy_code",
                                        "CATALOG" => "catalog",
                                        "FLOW" => "flow",
                                        "REMINDER" => "reminder",
                                        "ORDER_DETAILS" => "order_details",
                                        _ => "unknown"
                                    };

                                    string? paramValue =
                                        button.url != null ? button.url.ToString() :
                                        button.phone_number != null ? button.phone_number.ToString() :
                                        button.coupon_code != null ? button.coupon_code.ToString() :
                                        button.flow_id != null ? button.flow_id.ToString() :
                                        null;

                                    // If BSP marks dynamic examples like Meta, respect them; otherwise be lenient
                                    buttons.Add(new ButtonMetadataDto
                                    {
                                        Text = text,
                                        Type = btnType,
                                        SubType = subType,
                                        Index = index,
                                        ParameterValue = paramValue ?? ""
                                    });
                                }
                                catch { /* ignore per-button parsing issues */ }
                            }
                        }
                    }
                }

                int placeholderCount = Regex.Matches(body ?? "", "{{(.*?)}}").Count;

                list.Add(new TemplateMetadataDto
                {
                    Name = name,
                    Language = language,
                    Body = body,
                    PlaceholderCount = placeholderCount,
                    HasImageHeader = hasImageHeader,
                    ButtonParams = buttons
                });
            }

            return list;
        }

        public async Task<List<TemplateForUIResponseDto>> FetchAllTemplatesAsync()
        {
            var result = new List<TemplateForUIResponseDto>();

            var user = _httpContextAccessor.HttpContext.User;
            var businessId = user.GetBusinessId();
            _logger.LogInformation(" Fetching templates for BusinessId {BusinessId}", businessId);

            // 1) Load this business's active setting (provider can be Meta or Pinnacle)
            var setting = await _dbContext.WhatsAppSettings
                .AsNoTracking()
                .FirstOrDefaultAsync(s =>
                    s.IsActive &&
                    s.BusinessId == businessId);

            if (setting == null)
            {
                _logger.LogWarning(" No active WhatsApp setting for BusinessId {BusinessId}", businessId);
                return result;
            }

            try
            {
                string provider = setting.Provider?.ToLowerInvariant() ?? "";

                if (provider == "meta_cloud")
                {
                    //  Meta Cloud path  ApiToken + WabaId required
                    if (string.IsNullOrWhiteSpace(setting.ApiToken) || string.IsNullOrWhiteSpace(setting.WabaId))
                    {
                        _logger.LogWarning(" Missing ApiToken or WabaId for Meta Cloud (Biz {BusinessId})", businessId);
                        return result;
                    }

                    var baseUrl = (setting.ApiUrl?.TrimEnd('/') ?? "https://graph.facebook.com/v22.0");
                    var nextUrl = $"{baseUrl}/{setting.WabaId}/message_templates?limit=100";

                    while (!string.IsNullOrWhiteSpace(nextUrl))
                    {
                        using var req = new HttpRequestMessage(HttpMethod.Get, nextUrl);
                        req.Headers.Authorization = new AuthenticationHeaderValue("Bearer", setting.ApiToken);

                        using var res = await _httpClient.SendAsync(req);
                        var json = await res.Content.ReadAsStringAsync();

                        _logger.LogInformation(" Meta Template API (Biz {BusinessId}) payload:\n{Json}", businessId, json);

                        if (!res.IsSuccessStatusCode)
                        {
                            _logger.LogError(" Meta template fetch failed (Biz {BusinessId}): {Json}", businessId, json);
                            break;
                        }

                        result.AddRange(ParseMetaTemplates(json));
                        nextUrl = JsonConvert.DeserializeObject<dynamic>(json)?.paging?.next?.ToString();
                    }
                }
                else if (provider == "pinnacle")
                {
                    //  Pinnacle path  ApiKey + PhoneNumberId required
                    if (string.IsNullOrWhiteSpace(setting.ApiKey) || string.IsNullOrWhiteSpace(setting.PhoneNumberId))
                    {
                        _logger.LogWarning(" Missing ApiKey or PhoneNumberId for Pinnacle (Biz {BusinessId})", businessId);
                        return result;
                    }

                    var baseUrl = (setting.ApiUrl?.TrimEnd('/') ?? "https://partnersv1.pinbot.ai/v3");
                    var nextUrl = $"{baseUrl}/{setting.WabaId}/message_templates";

                    using var req = new HttpRequestMessage(HttpMethod.Get, nextUrl);
                    req.Headers.Add("apikey", setting.ApiKey);

                    using var res = await _httpClient.SendAsync(req);
                    var json = await res.Content.ReadAsStringAsync();

                    _logger.LogInformation(" Pinnacle Template API (Biz {BusinessId}) payload:\n{Json}", businessId, json);

                    if (!res.IsSuccessStatusCode)
                    {
                        _logger.LogError(" Pinnacle template fetch failed (Biz {BusinessId}): {Json}", businessId, json);
                        return result;
                    }

                    result.AddRange(ParsePinnacleTemplates(json));
                }
                else
                {
                    _logger.LogWarning(" Unknown provider '{Provider}' for Biz {BusinessId}", provider, businessId);
                }
            }
            catch (Exception ex)
            {
                _logger.LogError(ex, " Exception while fetching templates for BusinessId {BusinessId}", businessId);
            }

            return result;
        }
        private List<TemplateForUIResponseDto> ParseMetaTemplates(string json)
        {
            var list = new List<TemplateForUIResponseDto>();
            dynamic parsed = JsonConvert.DeserializeObject<dynamic>(json);

            foreach (var tpl in parsed.data)
            {
                string status = (tpl.status?.ToString() ?? "").ToUpperInvariant();
                if (status != "APPROVED" && status != "ACTIVE") continue;

                list.Add(BuildTemplateDtoFromComponents(tpl));
            }

            return list;
        }

        private List<TemplateForUIResponseDto> ParsePinnacleTemplates(string json)
        {
            var list = new List<TemplateForUIResponseDto>();
            dynamic parsed = JsonConvert.DeserializeObject<dynamic>(json);

            if (parsed?.data == null) return list;

            foreach (var tpl in parsed.data)
            {
                // Pinnacle may not use status like Meta, adjust filter if needed
                list.Add(BuildTemplateDtoFromComponents(tpl));
            }

            return list;
        }

        private TemplateForUIResponseDto BuildTemplateDtoFromComponents(dynamic tpl)
        {
            string name = tpl.name;
            string language = tpl.language ?? "en_US";
            string body = "";
            bool hasImageHeader = false;
            var buttons = new List<ButtonMetadataDto>();

            foreach (var component in tpl.components)
            {
                string type = component.type?.ToString()?.ToUpperInvariant();

                if (type == "BODY")
                    body = component.text?.ToString() ?? "";

                if (type == "HEADER" && (component.format?.ToString()?.ToUpperInvariant() == "IMAGE"))
                    hasImageHeader = true;

                if (type == "BUTTONS")
                {
                    foreach (var button in component.buttons)
                    {
                        string btnType = button.type?.ToString()?.ToUpperInvariant() ?? "";
                        string text = button.text?.ToString() ?? "";
                        int index = buttons.Count;

                        string subType = btnType switch
                        {
                            "URL" => "url",
                            "PHONE_NUMBER" => "voice_call",
                            "QUICK_REPLY" => "quick_reply",
                            "COPY_CODE" => "copy_code",
                            "CATALOG" => "catalog",
                            "FLOW" => "flow",
                            "REMINDER" => "reminder",
                            "ORDER_DETAILS" => "order_details",
                            _ => "unknown"
                        };

                        string? paramValue = button.url?.ToString() ?? button.phone_number?.ToString();

                        if (subType == "unknown") continue;

                        buttons.Add(new ButtonMetadataDto
                        {
                            Text = text,
                            Type = btnType,
                            SubType = subType,
                            Index = index,
                            ParameterValue = paramValue ?? ""
                        });
                    }
                }
            }

            int placeholderCount = Regex.Matches(body ?? "", "{{(.*?)}}").Count;

            return new TemplateForUIResponseDto
            {
                Name = name,
                Language = language,
                Body = body,
                ParametersCount = placeholderCount,
                HasImageHeader = hasImageHeader,
                ButtonParams = buttons
            };
        }

        public async Task<TemplateMetadataDto?> GetTemplateByNameAsync(Guid businessId, string templateName, bool includeButtons)
        {
            var setting = await _dbContext.WhatsAppSettings
                .FirstOrDefaultAsync(x => x.IsActive && x.BusinessId == businessId);

            if (setting == null)
            {
                _logger.LogWarning(" WhatsApp settings not found for business: {BusinessId}", businessId);
                return null;
            }

            var provider = (setting.Provider ?? "meta_cloud").Trim().ToLowerInvariant();
            var wabaId = setting.WabaId?.Trim();
            if (string.IsNullOrWhiteSpace(wabaId))
            {
                _logger.LogWarning(" Missing WABA ID for business: {BusinessId}", businessId);
                return null;
            }

            // Build URL + request with per-request headers
            string url;
            using var req = new HttpRequestMessage(HttpMethod.Get, "");

            if (provider == "pinnacle")
            {
                // Pinnacle: require ApiKey; use WabaId for template listing
                if (string.IsNullOrWhiteSpace(setting.ApiKey))
                {
                    _logger.LogWarning(" ApiKey missing for Pinnacle provider (BusinessId {BusinessId})", businessId);
                    return null;
                }

                var baseUrl = string.IsNullOrWhiteSpace(setting.ApiUrl)
                    ? "https://partnersv1.pinbot.ai/v3"
                    : setting.ApiUrl.TrimEnd('/');

                url = $"{baseUrl}/{wabaId}/message_templates?limit=200";
                // add header variants
                req.Headers.TryAddWithoutValidation("apikey", setting.ApiKey);
                req.Headers.TryAddWithoutValidation("x-api-key", setting.ApiKey);
                // safety: also append as query (some edges require it)
                url = url.Contains("apikey=") ? url : $"{url}&apikey={Uri.EscapeDataString(setting.ApiKey)}";
            }
            else // meta_cloud
            {
                // Meta Cloud: require ApiToken; use WabaId for template listing
                if (string.IsNullOrWhiteSpace(setting.ApiToken))
                {
                    _logger.LogWarning(" ApiToken missing for Meta provider (BusinessId {BusinessId})", businessId);
                    return null;
                }

                var baseUrl = string.IsNullOrWhiteSpace(setting.ApiUrl)
                    ? "https://graph.facebook.com/v18.0"
                    : setting.ApiUrl.TrimEnd('/');

                url = $"{baseUrl}/{wabaId}/message_templates?limit=200";
                req.Headers.Authorization = new AuthenticationHeaderValue("Bearer", setting.ApiToken);
            }

            req.RequestUri = new Uri(url);
            var response = await _httpClient.SendAsync(req);
            var json = await response.Content.ReadAsStringAsync();

            if (!response.IsSuccessStatusCode)
            {
                _logger.LogError(" Failed to fetch templates (provider={Provider}) for BusinessId {BusinessId}: HTTP {Status} Body: {Body}",
                    provider, businessId, (int)response.StatusCode, json);
                return null;
            }

            try
            {
                dynamic parsed = JsonConvert.DeserializeObject<dynamic>(json);
                var data = parsed?.data;
                if (data == null)
                {
                    _logger.LogWarning(" No 'data' array in template response (provider={Provider})", provider);
                    return null;
                }

                foreach (var tpl in data)
                {
                    string name = tpl.name;
                    if (!name.Equals(templateName, StringComparison.OrdinalIgnoreCase))
                        continue;

                    string language = tpl.language != null ? (string)tpl.language : "en_US";
                    string body = "";
                    var buttons = new List<ButtonMetadataDto>();
                    bool hasImageHeader = false;

                    // components loop
                    foreach (var component in tpl.components)
                    {
                        string type = component.type?.ToString()?.ToUpperInvariant();

                        if (type == "BODY")
                        {
                            try { body = component.text?.ToString() ?? ""; }
                            catch { body = ""; }
                        }

                        if (type == "HEADER")
                        {
                            string format = component.format?.ToString()?.ToUpperInvariant();
                            if (format == "IMAGE") hasImageHeader = true;
                        }

                        if (includeButtons && type == "BUTTONS")
                        {
                            foreach (var button in component.buttons)
                            {
                                try
                                {
                                    string btnType = button.type?.ToString()?.ToUpperInvariant() ?? "";
                                    string text = button.text?.ToString() ?? "";
                                    int index = buttons.Count;

                                    // normalize sub-type for our app
                                    string subType = btnType switch
                                    {
                                        "URL" => "url",
                                        "PHONE_NUMBER" => "voice_call",
                                        "QUICK_REPLY" => "quick_reply",
                                        "COPY_CODE" => "copy_code",
                                        "CATALOG" => "catalog",
                                        "FLOW" => "flow",
                                        "REMINDER" => "reminder",
                                        "ORDER_DETAILS" => "order_details",
                                        _ => "unknown"
                                    };

                                    // Known dynamic param extraction
                                    string? paramValue = null;
                                    if (button.url != null)
                                        paramValue = button.url.ToString();
                                    else if (button.phone_number != null)
                                        paramValue = button.phone_number.ToString();
                                    else if (button.coupon_code != null)
                                        paramValue = button.coupon_code.ToString();
                                    else if (button.flow_id != null)
                                        paramValue = button.flow_id.ToString();

                                    // Skip truly invalid
                                    if (subType == "unknown" ||
                                        (paramValue == null && new[] { "url", "flow", "copy_code" }.Contains(subType)))
                                    {
                                        _logger.LogWarning(" Skipping button '{Text}' due to unknown type or missing required param.", text);
                                        continue;
                                    }

                                    buttons.Add(new ButtonMetadataDto
                                    {
                                        Text = text,
                                        Type = btnType,
                                        SubType = subType,
                                        Index = index,
                                        ParameterValue = paramValue ?? "" // empty for static buttons
                                    });
                                }
                                catch (Exception exBtn)
                                {
                                    _logger.LogWarning(exBtn, " Failed to parse button in template {TemplateName}", name);
                                }
                            }
                        }
                    }

                    // Count {{n}} placeholders in body
                    int paramCount = Regex.Matches(body ?? "", "{{\\s*\\d+\\s*}}").Count;

                    return new TemplateMetadataDto
                    {
                        Name = name,
                        Language = language,
                        Body = body,
                        PlaceholderCount = paramCount,
                        HasImageHeader = hasImageHeader,
                        ButtonParams = includeButtons ? buttons : new List<ButtonMetadataDto>()
                    };
                }
            }
            catch (Exception ex)
            {
                _logger.LogError(ex, " Exception while parsing template response");
            }

            return null;
        }
        private static TemplateMetadataDto? ExtractTemplateFromListJson(string json, string templateName, bool includeButtons)
        {
            var root = JObject.Parse(json);
            var data = root["data"] as JArray;
            if (data == null) return null;

            foreach (var tplToken in data.OfType<JObject>())
            {
                var name = tplToken.Value<string>("name") ?? "";
                if (!name.Equals(templateName, StringComparison.OrdinalIgnoreCase))
                    continue;

                var language = tplToken.Value<string>("language") ?? "en_US";
                var components = tplToken["components"] as JArray;

                string body = "";
                bool hasImageHeader = false;
                var buttons = new List<ButtonMetadataDto>();

                if (components != null)
                {
                    foreach (var comp in components.OfType<JObject>())
                    {
                        var type = (comp.Value<string>("type") ?? "").ToUpperInvariant();

                        if (type == "BODY")
                        {
                            body = comp.Value<string>("text") ?? body;
                        }
                        else if (type == "HEADER")
                        {
                            var fmt = (comp.Value<string>("format") ?? "").ToUpperInvariant();
                            if (fmt == "IMAGE") hasImageHeader = true;
                        }
                        else if (includeButtons && type == "BUTTONS")
                        {
                            var btns = comp["buttons"] as JArray;
                            if (btns == null) continue;

                            var idx = 0;
                            foreach (var b in btns.OfType<JObject>())
                            {
                                var btnTypeRaw = (b.Value<string>("type") ?? "").ToUpperInvariant();
                                var text = b.Value<string>("text") ?? "";

                                var subType = btnTypeRaw switch
                                {
                                    "URL" => "url",
                                    "PHONE_NUMBER" => "voice_call",
                                    "QUICK_REPLY" => "quick_reply",
                                    "COPY_CODE" => "copy_code",
                                    "CATALOG" => "catalog",
                                    "FLOW" => "flow",
                                    "REMINDER" => "reminder",
                                    "ORDER_DETAILS" => "order_details",
                                    _ => "unknown"
                                };

                                string? paramValue =
                                    b.Value<string>("url") ??
                                    b.Value<string>("phone_number") ??
                                    b.Value<string>("coupon_code") ??
                                    b.Value<string>("flow_id");

                                // Skip unknown or missing required dynamic values
                                if (subType == "unknown") continue;
                                if ((subType is "url" or "flow" or "copy_code") && string.IsNullOrWhiteSpace(paramValue))
                                    continue;

                                buttons.Add(new ButtonMetadataDto
                                {
                                    Text = text,
                                    Type = btnTypeRaw,
                                    SubType = subType,
                                    Index = idx++,
                                    ParameterValue = paramValue ?? ""
                                });
                            }
                        }
                    }
                }

                var paramCount = Regex.Matches(body ?? "", "{{(.*?)}}").Count;

                return new TemplateMetadataDto
                {
                    Name = name,
                    Language = language,
                    Body = body ?? "",
                    PlaceholderCount = paramCount,
                    HasImageHeader = hasImageHeader,
                    ButtonParams = includeButtons ? buttons : new List<ButtonMetadataDto>()
                };
            }

            return null;
        }

    }
}

//  File: WhatsAppSettings/Validators/SaveWhatsAppSettingValidator.cs
using FluentValidation;
using xbytechat_api.WhatsAppSettings.DTOs;

namespace xbytechat_api.WhatsAppSettings.Validators
{
    public class SaveWhatsAppSettingValidator : AbstractValidator<SaveWhatsAppSettingDto>
    {
        public SaveWhatsAppSettingValidator()
        {
            RuleFor(x => x.Provider)
                .NotEmpty().WithMessage("Provider is required.")
                .Must(p => p == "pinnacle" || p == "meta_cloud")
                .WithMessage("Provider must be 'pinnacle' or 'meta_cloud'.");

            RuleFor(x => x.ApiUrl)
                .NotEmpty().WithMessage("API URL is required.");

            // Meta Cloud requirements
            When(x => x.Provider == "meta_cloud", () =>
            {
                RuleFor(x => x.ApiToken)
                    .NotEmpty().WithMessage("API Token is required for Meta Cloud.");
                RuleFor(x => x.PhoneNumberId)
                    .NotEmpty().WithMessage("Phone Number ID is required for Meta Cloud.");
            });

            // Pinbot requirements
            When(x => x.Provider == "pinnacle", () =>
            {
                RuleFor(x => x.ApiKey)
                    .NotEmpty().WithMessage("API Key is required for Pinbot.");
                RuleFor(x => x)
                    .Must(x => !string.IsNullOrWhiteSpace(x.PhoneNumberId) || !string.IsNullOrWhiteSpace(x.WabaId))
                    .WithMessage("Provide Phone Number ID or WABA ID for Pinbot.");
            });
        }
    }
}

using xbytechat.api.Features.AuditTrail.Models;
using xbytechat.api.Features.AuditTrail.Services;

public static class AuditLoggingHelper
{
    private static IServiceProvider? _serviceProvider;

    public static void Configure(IServiceProvider serviceProvider)
    {
        _serviceProvider = serviceProvider;
    }

    public static void Log(
        string actionType,
        string? entityName,
        string? entityId,
        string? description,
        IHttpContextAccessor contextAccessor)
    {
        if (_serviceProvider == null) return;

        var scope = _serviceProvider.CreateScope();
        var auditLogService = scope.ServiceProvider.GetRequiredService<IAuditLogService>();

        var httpContext = contextAccessor.HttpContext;
        var user = httpContext?.User;
        var claims = user?.Identities?.FirstOrDefault();

        var log = new AuditLog
        {
            Id = Guid.NewGuid(),
            ActionType = actionType,
            Description = description,
            BusinessId = TryParseGuid(claims?.FindFirst("businessId")?.Value),
            PerformedByUserId = TryParseGuid(claims?.FindFirst("sub")?.Value),
            PerformedByUserName = claims?.FindFirst("email")?.Value,
            RoleAtTime = claims?.FindFirst("role")?.Value,
            IPAddress = httpContext?.Connection?.RemoteIpAddress?.ToString(),
            UserAgent = httpContext?.Request?.Headers["User-Agent"].ToString(),
            CreatedAt = DateTime.UtcNow
        };

        _ = Task.Run(() => auditLogService.SaveLogAsync(log));
    }

    private static Guid TryParseGuid(string? input) =>
        Guid.TryParse(input, out var guid) ? guid : Guid.Empty;
}

//  File: Helpers/JwtCookieHelper.cs
using Microsoft.AspNetCore.Http;
using System;

namespace xbytechat.api.Helpers
{
    public static class JwtCookieHelper
    {
        //  Set Access Token (short-lived)
        public static void SetJwtCookie(HttpContext httpContext, string cookieName, string token, int expiryHours = 12)
        {
            if (httpContext == null || httpContext.Response.HasStarted)
            {
                Console.WriteLine($" Cannot set JWT cookie '{cookieName}'  response already started.");
                return;
            }

            bool isProduction = Environment.GetEnvironmentVariable("ASPNETCORE_ENVIRONMENT") == "Production";

            httpContext.Response.Cookies.Append(cookieName, token, new CookieOptions
            {
                HttpOnly = true,
                ///*Secure*/ = isProduction,
                Secure = true,
                SameSite = SameSiteMode.Strict,
                Expires = DateTimeOffset.UtcNow.AddHours(expiryHours)
            }); ;
        }

        //  Clear Access Token cookie
        public static void ClearJwtCookie(HttpContext httpContext, string cookieName)
        {
            if (httpContext == null || httpContext.Response.HasStarted)
            {
                Console.WriteLine($" Cannot clear JWT cookie '{cookieName}'  response already started.");
                return;
            }

            httpContext.Response.Cookies.Append(cookieName, "", new CookieOptions
            {
                HttpOnly = true,
                Secure = true,
                SameSite = SameSiteMode.Strict,
                Expires = DateTimeOffset.UtcNow.AddDays(-1)
            });
        }

        //  Set Refresh Token (long-lived)
        public static void SetRefreshTokenCookie(HttpContext httpContext, string cookieName, string refreshToken, int expiryDays = 30)
        {
            if (httpContext == null || httpContext.Response.HasStarted)
            {
                Console.WriteLine($" Cannot set refresh cookie '{cookieName}'  response already started.");
                return;
            }

            bool isProduction = Environment.GetEnvironmentVariable("ASPNETCORE_ENVIRONMENT") == "Production";

            httpContext.Response.Cookies.Append(cookieName, refreshToken, new CookieOptions
            {
                HttpOnly = true,
                Secure = isProduction,
                SameSite = SameSiteMode.Strict,
                Expires = DateTimeOffset.UtcNow.AddDays(expiryDays)
            });
        }

        //  Clear Refresh Token cookie
        public static void ClearRefreshTokenCookie(HttpContext httpContext, string cookieName)
        {
            if (httpContext == null || httpContext.Response.HasStarted)
            {
                Console.WriteLine($" Cannot clear refresh cookie '{cookieName}'  response already started.");
                return;
            }

            httpContext.Response.Cookies.Append(cookieName, "", new CookieOptions
            {
                HttpOnly = true,
                Secure = true,
                SameSite = SameSiteMode.Strict,
                Expires = DateTimeOffset.UtcNow.AddDays(-1)
            });
        }
    }
}

namespace xbytechat.api.Helpers
{
    /// <summary>
    /// Represents a standardized response structure for service layer results.
    /// </summary>
    public class ResponseResult
    {
        public bool Success { get; set; }                  //  Whether operation succeeded
        public string Message { get; set; }                //  User-friendly message
        public object? Data { get; set; }                  // Optional payload (if needed)

        //  WhatsApp-specific diagnostics
        public string? ErrorMessage { get; set; }          // Error from API or exception
        public string? RawResponse { get; set; }           // Full API raw response

        public string? MessageId { get; set; } //  WhatsApp WAMID (Message ID)

        public Guid? LogId { get; set; } //  Unique ID of MessageLog for tracking
                                         //  Factory method for successful result

        public string? Token { get; set; }

        public string? RefreshToken { get; set; }
        public static ResponseResult SuccessInfo(string message, object? data = null, string? raw = null)
        {
            return new ResponseResult
            {
                Success = true,
                Message = message,
                Data = data,
                RawResponse = raw
            };
        }

        //  Factory method for error result
        public static ResponseResult ErrorInfo(string message, string? error = null, string? raw = null)
        {
            return new ResponseResult
            {
                Success = false,
                Message = message,
                ErrorMessage = error,
                RawResponse = raw
            };
        }
    }
}

